import React from 'react';
import Image from './component/Image';
import fun_cat_1 from './img/fun_cat/fun_cat_1.jpg';
import fun_cat_2 from './img/fun_cat/fun_cat_2.jpg';
import fun_cat_3 from './img/fun_cat/fun_cat_3.jpg';
import fun_cat_4 from './img/fun_cat/fun_cat_4.jpg';
import fun_cat_5 from './img/fun_cat/fun_cat_5.jpg';
import fun_cat_6 from './img/fun_cat/fun_cat_6.jpg';

class Input extends React.Component {
  // Метод helloPrint
  helloPrint() {
    return console.log('hello! onClick');
  }

  onMouse() {
    return console.log('hello! onMouse');
  }

  // Переменная par
  par = 'Введите текст';
  
  // Метод render
  render() {
    return (
      <input 
        placeholder={this.par} 
        onClick={this.helloPrint.bind(this)} 
        onMouseEnter={this.onMouse.bind(this)}
      />
    );
  }
}

function SampleText() {
  return (<p>Шаблон для вашего текста</p>);
}

class NewText extends React.Component {
  render() {
    return (<p>{this.props.title}</p>);
  }
}
function App_() {
  // const funCat = require('./img/fun_cat/fun_cat.jpg');
  const images = [fun_cat_1, fun_cat_2, fun_cat_3, fun_cat_4, fun_cat_5,fun_cat_6];

  return (
    <div className='App'>
      <Input />
      {/* <div className="image-container">
      {images.map((image, index) => (
        <Image key={index} image={image} />
      ))}
      </div> */}

<div className="image-container">
        <Image image={fun_cat_1} />
        <Image image={fun_cat_2} />
        <Image image={fun_cat_3} />
        <Image image={fun_cat_4} />
        <Image image={fun_cat_5} />
        <Image image={fun_cat_6} />
      </div>

      {/* <Image image={fun_cat_1}/> */}
    </div>
  );
}

export default App_;
