import React from 'react';
import ReactDOM from 'react-dom/client'; // Убедитесь, что импорт правильный

const helloPrint = () => {
  console.log('hello! onClick');
}

const onMouse = () => {
  console.log('hello! onMouse');
}


function Input() {
  let par = 'priv how a u';
  return(
    <input 
          placeholder={par} 
          onClick={helloPrint} 
          onMouseEnter={onMouse}
        />)
}
function emptyText() {
  return (<p>Привет это текст текст</p>)
}
function App() {
  return (
    <div className='App'>
      <Input/>
      <emptyText/>
    </div>

  );
}

// Создаем корень с помощью createRoot
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />); // Рендерим компонент App
