}

const onMouse = () => {
  console.log('hello! onMouse');
}


const Input=() =>{
  let par = 'priv how a u';
  return(
    <input 
          placeholder={par} 
          onClick={helloPrint} 
          onMouseEnter={onMouse}
        />)
}
function EmmtyText() {
