import React from 'react';

// Предполагается, что изображения находятся в папке public/img/fun_cat/
const imagePaths = [
  'fun_cat_1.jpg',
  'fun_cat_2.jpg',
  'fun_cat_3.jpg',
  'fun_cat_4.jpg',
  'fun_cat_5.jpg',
  'fun_cat_6.jpg'
];

class Image extends React.Component {
  render() {
    const { imageIndex } = this.props;
    const imagePath = imagePaths[imageIndex - 1];
    
    return (
      <img 
        image={`./img/fun_cat/${imagePath}`} 
        alt={`Cute Image ${imageIndex + 1}`} 
        className="cute-image" 
      />
    );
  }
}

export default Image;
