import React from 'react';
import 'bootstrap-icons/font/bootstrap-icons.css'; // Убедитесь, что стили Bootstrap Icons подключены
import { BsXCircle ,BsFillPencilFill  } from "react-icons/bs";
export class User extends React.Component {
  constructor(props) {
    super(props);
    this.user = this.props.user;
    this.state = {
      isEditing: false,
      editedUser: { ...this.props.user },
    };
  }
  handleDelete = () => {
    const { onDelete, user } = this.props;
    if (onDelete) {
      onDelete(user.id); // Передаем id пользователя в родительский компонент
    }
  };
  // handleEdit = () => {
  //   const { onEdit, user } = this.props;
  //   if (onEdit) {
  //     onEdit(user.id); 
  //   }
  // };
  handleEdit = () => {
    this.setState({ isEditing: true });
  };
  handleSave = () => {
    const { onEdit, user } = this.props;
    if (onEdit) {
      onEdit(user.id, this.state.editedUser);
      this.setState({ isEditing: false });
    }
  };

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState(prevState => ({
      editedUser: {
        ...prevState.editedUser,
        [name]: value,
      },
    }));
  };
  render() {
    return (
      <div className="user-card">
        <div className="icon-container">

        <BsFillPencilFill className='edit-icon' onClick={this.handleEdit} />
        <BsXCircle  className='close-icon' onClick={this.onhandleDelete}/>
        </div>

        <img src={this.user.image} alt={this.user.name} className="user-image" />

        <h2>{this.user.name}</h2>
        <p>{this.user.bio}</p>
      </div>
    );
  }
}

export default User;
